import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import { spawn } from "child_process";
import path from "path";
import { fileURLToPath } from 'url';
import express from "express";
import { setupVite } from "./vite";
import fs from "fs";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const isProduction = process.env.NODE_ENV === "production";

if (isProduction) {
  console.log("=" .repeat(60));
  console.log("Starting Production Server");
  console.log("=" .repeat(60));
  console.log("");

  // In production, just start the Python server
  // It will serve both API and static files from dist/public
  const pythonServerPath = path.join(__dirname, "..", "python_server");

  console.log("Starting Python backend on port 5000...");
  console.log("The Python server will serve the built frontend and API");
  console.log("");

  const pythonServer = spawn("python3", ["main.py"], {
    cwd: pythonServerPath,
    stdio: "inherit",
    env: { ...process.env, PORT: "5000" }
  });

  pythonServer.on("error", (error) => {
    console.error("Failed to start Python server:", error);
    process.exit(1);
  });

  pythonServer.on("close", (code) => {
    console.log(`Python server exited with code ${code}`);
    process.exit(code || 0);
  });

  process.on("SIGINT", () => {
    console.log("\nShutting down server...");
    pythonServer.kill("SIGINT");
    process.exit(0);
  });

  process.on("SIGTERM", () => {
    console.log("\nShutting down server...");
    pythonServer.kill("SIGTERM");
    process.exit(0);
  });
} else {
  console.log("=" .repeat(60));
  console.log("Starting Vite Dev Server + Python Backend");
  console.log("=" .repeat(60));
  console.log("");

  // Create Express app for Vite
  const app = express();

  // Setup Vite for development on port 5173
  const server = (await import('http')).createServer(app);
  await setupVite(app, server);

  // Start Vite server on port 5173
  const port = 5173;
  server.listen(port, "0.0.0.0", () => {
    console.log(`âœ“ Vite dev server running on http://localhost:${port}`);
    console.log("");
  });

  // Start Python server on port 5000 (main port)
  console.log("Starting Python backend on port 5000...");
  const pythonServerPath = path.join(__dirname, "..", "python_server");

  const pythonServer = spawn("python3", ["main.py"], {
    cwd: pythonServerPath,
    stdio: "inherit",
    env: { ...process.env, PORT: "5000" }
  });

  pythonServer.on("error", (error) => {
    console.error("Failed to start Python server:", error);
    process.exit(1);
  });

  pythonServer.on("close", (code) => {
    console.log(`Python server exited with code ${code}`);
    process.exit(code || 0);
  });

  // Handle process termination
  process.on("SIGINT", () => {
    console.log("\nShutting down servers...");
    pythonServer.kill("SIGINT");
    process.exit(0);
  });

  process.on("SIGTERM", () => {
    console.log("\nShutting down servers...");
    pythonServer.kill("SIGTERM");
    process.exit(0);
  });
}

server/objectAcl.ts.content:
import { File } from "@google-cloud/storage";

const ACL_POLICY_METADATA_KEY = "custom:aclPolicy";

// The type of the access group.
//
// Can be flexibly defined according to the use case.
//
// Examples:
// - USER_LIST: the users from a list stored in the database;
// - EMAIL_DOMAIN: the users whose email is in a specific domain;
// - GROUP_MEMBER: the users who are members of a specific group;
// - SUBSCRIBER: the users who are subscribers of a specific service / content
//   creator.
export enum ObjectAccessGroupType {}

// The logic user group that can access the object.
export interface ObjectAccessGroup {
  // The type of the access group.
  type: ObjectAccessGroupType;
  // The logic id that is enough to identify the qualified group members.
  //
  // It may have different format for different types. For example:
  // - for USER_LIST, the id could be the user list db entity id, and the
  //   user list db entity could contain a bunch of user ids. User needs
  //   to be a member of the user list to be able to access the object.
  // - for EMAIL_DOMAIN, the id could be the email domain, and the user needs
  //   to have an email with the domain to be able to access the object.
  // - for GROUP_MEMBER, the id could be the group db entity id, and the
  //   group db entity could contain a bunch of user ids. User needs to be
  //   a member of the group to be able to access the object.
  // - for SUBSCRIBER, the id could be the subscriber db entity id, and the
  //   subscriber db entity could contain a bunch of user ids. User needs to
  //   be a subscriber to be able to access the object.
  id: string;
}

export enum ObjectPermission {
  READ = "read",
  WRITE = "write",
}

export interface ObjectAclRule {
  group: ObjectAccessGroup;
  permission: ObjectPermission;
}

// The ACL policy of the object.
// This would be set as part of the object custom metadata:
// - key: "custom:aclPolicy"
// - value: JSON string of the ObjectAclPolicy object.
export interface ObjectAclPolicy {
  owner: string;
  visibility: "public" | "private";
  aclRules?: Array<ObjectAclRule>;
}

// Check if the requested permission is allowed based on the granted permission.
function isPermissionAllowed(
  requested: ObjectPermission,
  granted: ObjectPermission,
): boolean {
  // Users granted with read or write permissions can read the object.
  if (requested === ObjectPermission.READ) {
    return [ObjectPermission.READ, ObjectPermission.WRITE].includes(granted);
  }

  // Only users granted with write permissions can write the object.
  return granted === ObjectPermission.WRITE;
}

// The base class for all access groups.
//
// Different types of access groups can be implemented according to the use case.
abstract class BaseObjectAccessGroup implements ObjectAccessGroup {
  constructor(
    public readonly type: ObjectAccessGroupType,
    public readonly id: string,
  ) {}

  // Check if the user is a member of the group.
  public abstract hasMember(userId: string): Promise<boolean>;
}

function createObjectAccessGroup(
  group: ObjectAccessGroup,
): BaseObjectAccessGroup {
  switch (group.type) {
    // Implement the case for each type of access group to instantiate.
    //
    // For example:
    // case "USER_LIST":
    //   return new UserListAccessGroup(group.id);
    // case "EMAIL_DOMAIN":
    //   return new EmailDomainAccessGroup(group.id);
    // case "GROUP_MEMBER":
    //   return new GroupMemberAccessGroup(group.id);
    // case "SUBSCRIBER":
    //   return new SubscriberAccessGroup(group.id);
    default:
      throw new Error(`Unknown access group type: ${group.type}`);
  }
}

// Sets the ACL policy to the object metadata.
export async function setObjectAclPolicy(
  objectFile: File,
  aclPolicy: ObjectAclPolicy,
): Promise<void> {
  const [exists] = await objectFile.exists();
  if (!exists) {
    throw new Error(`Object not found: ${objectFile.name}`);
  }

  await objectFile.setMetadata({
    metadata: {
      [ACL_POLICY_METADATA_KEY]: JSON.stringify(aclPolicy),
    },
  });
}

// Gets the ACL policy from the object metadata.
export async function getObjectAclPolicy(
  objectFile: File,
): Promise<ObjectAclPolicy | null> {
  const [metadata] = await objectFile.getMetadata();
  const aclPolicy = metadata?.metadata?.[ACL_POLICY_METADATA_KEY];
  if (!aclPolicy) {
    return null;
  }
  return JSON.parse(aclPolicy as string);
}

// Checks if the user can access the object.
export async function canAccessObject({
  userId,
  objectFile,
  requestedPermission,
}: {
  userId?: string;
  objectFile: File;
  requestedPermission: ObjectPermission;
}): Promise<boolean> {
  // When this function is called, the acl policy is required.
  const aclPolicy = await getObjectAclPolicy(objectFile);
  if (!aclPolicy) {
    return false;
  }

  // Public objects are always accessible for read.
  if (
    aclPolicy.visibility === "public" &&
    requestedPermission === ObjectPermission.READ
  ) {
    return true;
  }

  // Access control requires the user id.
  if (!userId) {
    return false;
  }

  // The owner of the object can always access it.
  if (aclPolicy.owner === userId) {
    return true;
  }

  // Go through the ACL rules to check if the user has the required permission.
  for (const rule of aclPolicy.aclRules || []) {
    const accessGroup = createObjectAccessGroup(rule.group);
    if (
      (await accessGroup.hasMember(userId)) &&
      isPermissionAllowed(requestedPermission, rule.permission)
    ) {
      return true;
    }
  }

  return false;
}

server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";
import {
  ObjectAclPolicy,
  ObjectPermission,
  canAccessObject,
  getObjectAclPolicy,
  setObjectAclPolicy,
} from "./objectAcl";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();
      // Get the ACL policy for the object.
      const aclPolicy = await getObjectAclPolicy(file);
      const isPublic = aclPolicy?.visibility === "public";
      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `${
          isPublic ? "public" : "private"
        }, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an object entity.
  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the object entity file from the object path.
  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(
    rawPath: string,
  ): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    // Extract the entity ID from the path
    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  // Tries to set the ACL policy for the object entity and return the normalized path.
  async trySetObjectEntityAclPolicy(
    rawPath: string,
    aclPolicy: ObjectAclPolicy
  ): Promise<string> {
    const normalizedPath = this.normalizeObjectEntityPath(rawPath);
    if (!normalizedPath.startsWith("/")) {
      return normalizedPath;
    }

    const objectFile = await this.getObjectEntityFile(normalizedPath);
    await setObjectAclPolicy(objectFile, aclPolicy);
    return normalizedPath;
  }

  // Checks if the user can access the object entity.
  async canAccessObjectEntity({
    userId,
    objectFile,
    requestedPermission,
  }: {
    userId?: string;
    objectFile: File;
    requestedPermission?: ObjectPermission;
  }): Promise<boolean> {
    return canAccessObject({
      userId,
      objectFile,
      requestedPermission: requestedPermission ?? ObjectPermission.READ,
    });
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

server/openai.ts.content:
import OpenAI from "openai";

const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || "" 
});

interface DocumentContext {
  name: string;
  content: string;
  category: string;
}

export async function askHRAssistant(
  question: string,
  documents: DocumentContext[]
): Promise<{ answer: string; documentsUsed: string[] }> {
  try {
    if (!process.env.OPENAI_API_KEY) {
      throw new Error("OpenAI API key is not configured. Please add your OPENAI_API_KEY to use the AI Assistant.");
    }

    const context = documents
      .map(doc => `Document: ${doc.name} (${doc.category})\nContent: ${doc.content}`)
      .join('\n\n---\n\n');

    const systemPrompt = `You are an AI HR Assistant for an employee self-service portal. Your role is to answer HR-related questions based on the provided company documents and policies.

Guidelines:
- Always be helpful, professional, and accurate
- Reference specific policy documents when applicable
- If you don't have enough information, say so clearly
- Provide actionable advice when possible
- Keep responses concise but comprehensive
- Format your response clearly with bullet points or sections when appropriate

Available Documents:
${context}`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        { role: "system", content: systemPrompt },
        { role: "user", content: question }
      ],
      max_tokens: 1000,
    });

    const answer = response.choices[0].message.content || "I apologize, but I couldn't generate a response to your question.";

    // Determine which documents were likely used based on the response
    const documentsUsed = documents
      .filter(doc => answer.toLowerCase().includes(doc.name.toLowerCase()) || 
                    answer.toLowerCase().includes(doc.category.toLowerCase()))
      .map(doc => doc.name);

    return {
      answer,
      documentsUsed: documentsUsed.length > 0 ? documentsUsed : [documents[0]?.name].filter(Boolean)
    };

  } catch (error) {
    console.error("Error calling OpenAI API:", error);
    if (error instanceof Error && error.message.includes("OpenAI API key is not configured")) {
      throw error;
    }
    throw new Error("Failed to get AI response. Please try again later.");
  }
}

export async function processDocumentForVectorization(
  documentContent: string,
  documentName: string
): Promise<string[]> {
  try {
    if (!process.env.OPENAI_API_KEY) {
      // Fallback: simple text splitting when API key is not available
      const chunks = documentContent.split('\n\n').filter(chunk => chunk.trim().length > 50);
      return chunks.slice(0, 50);
    }

    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "Break down this HR document into meaningful chunks for vector storage. Each chunk should be self-contained and cover a specific topic or policy. Return the chunks as a JSON array of strings."
        },
        {
          role: "user", 
          content: `Document: ${documentName}\n\nContent: ${documentContent}`
        }
      ],
      response_format: { type: "json_object" },
      max_tokens: 2000,
    });

    const result = JSON.parse(response.choices[0].message.content || '{"chunks": []}');
    return result.chunks || [];

  } catch (error) {
    console.error("Error processing document:", error);
    // Fallback: simple text splitting
    const chunks = documentContent.split('\n\n').filter(chunk => chunk.trim().length > 50);
    return chunks.slice(0, 50); // Limit to 50 chunks per document
  }
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { ObjectStorageService } from "./objectStorage";
import { askHRAssistant, processDocumentForVectorization } from "./openai";
import { insertLeaveSchema, insertAttendanceSchema, insertHrDocumentSchema, insertAiConversationSchema } from "@shared/schema";
import { z } from "zod";
import multer from "multer";
import path from "path";
import fs from "fs";

const upload = multer({ dest: 'uploads/' });

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Dashboard stats
  app.get('/api/dashboard/stats', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const currentYear = new Date().getFullYear();
      const currentMonth = new Date().getMonth() + 1;

      const [leaveBalances, leaves, attendanceRecords] = await Promise.all([
        storage.getLeaveBalances(userId, currentYear),
        storage.getUserLeaves(userId),
        storage.getAttendanceRecords(userId, currentMonth, currentYear)
      ]);

      const totalLeavesUsed = leaveBalances.reduce((sum, balance) => sum + (balance.usedDays ?? 0), 0);
      const totalLeavesRemaining = leaveBalances.reduce((sum, balance) => sum + (balance.totalDays - (balance.usedDays ?? 0)), 0);

      const presentDays = attendanceRecords.filter(record => record.status === 'present' || record.status === 'wfh').length;
      const totalWorkingDays = attendanceRecords.length;
      const attendanceRate = totalWorkingDays > 0 ? (presentDays / totalWorkingDays * 100).toFixed(1) : "0.0";

      const pendingLeaves = leaves.filter(leave => leave.status === 'pending').length;

      res.json({
        leavesUsed: totalLeavesUsed,
        leavesRemaining: totalLeavesRemaining,
        attendanceRate: parseFloat(attendanceRate),
        pendingRequests: pendingLeaves,
        leaveBalances: leaveBalances.map(balance => ({
          type: balance.leaveTypeId,
          used: balance.usedDays ?? 0,
          total: balance.totalDays,
          remaining: balance.totalDays - (balance.usedDays ?? 0)
        }))
      });
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // Leave management routes
  app.get('/api/leave-types', isAuthenticated, async (req, res) => {
    try {
      const leaveTypes = await storage.getLeaveTypes();
      res.json(leaveTypes);
    } catch (error) {
      console.error("Error fetching leave types:", error);
      res.status(500).json({ message: "Failed to fetch leave types" });
    }
  });

  app.get('/api/leave-balances', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const year = parseInt(req.query.year as string) || new Date().getFullYear();
      const balances = await storage.getLeaveBalances(userId, year);
      res.json(balances);
    } catch (error) {
      console.error("Error fetching leave balances:", error);
      res.status(500).json({ message: "Failed to fetch leave balances" });
    }
  });

  app.post('/api/leaves', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const leaveData = insertLeaveSchema.parse({ ...req.body, userId });

      // Calculate days between dates
      const fromDate = new Date(leaveData.fromDate);
      const toDate = new Date(leaveData.toDate);
      const timeDiff = toDate.getTime() - fromDate.getTime();
      const daysDiff = Math.ceil(timeDiff / (1000 * 3600 * 24)) + 1;

      const leave = await storage.createLeave({
        ...leaveData,
        days: daysDiff.toString()
      });

      res.json(leave);
    } catch (error) {
      console.error("Error creating leave:", error);
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid leave data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create leave request" });
      }
    }
  });

  app.get('/api/leaves', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const leaves = await storage.getUserLeaves(userId);
      res.json(leaves);
    } catch (error) {
      console.error("Error fetching leaves:", error);
      res.status(500).json({ message: "Failed to fetch leaves" });
    }
  });

  app.put('/api/leaves/:id', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;
      const updatedLeave = await storage.updateLeave(id, updates);
      res.json(updatedLeave);
    } catch (error) {
      console.error("Error updating leave:", error);
      res.status(500).json({ message: "Failed to update leave" });
    }
  });

  app.delete('/api/leaves/:id', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      await storage.deleteLeave(id);
      res.json({ message: "Leave deleted successfully" });
    } catch (error) {
      console.error("Error deleting leave:", error);
      res.status(500).json({ message: "Failed to delete leave" });
    }
  });

  // Attendance routes
  app.get('/api/attendance', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const month = parseInt(req.query.month as string) || new Date().getMonth() + 1;
      const year = parseInt(req.query.year as string) || new Date().getFullYear();

      const records = await storage.getAttendanceRecords(userId, month, year);

      // Calculate stats
      const stats = {
        present: records.filter(r => r.status === 'present').length,
        absent: records.filter(r => r.status === 'absent').length,
        leave: records.filter(r => r.status === 'leave').length,
        wfh: records.filter(r => r.status === 'wfh').length
      };

      res.json({ records, stats });
    } catch (error) {
      console.error("Error fetching attendance:", error);
      res.status(500).json({ message: "Failed to fetch attendance records" });
    }
  });

  app.get('/api/attendance/absent-dates', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const days = parseInt(req.query.days as string) || 7;
      const absentDates = await storage.getAbsentDates(userId, days);
      res.json(absentDates);
    } catch (error) {
      console.error("Error fetching absent dates:", error);
      res.status(500).json({ message: "Failed to fetch absent dates" });
    }
  });

  app.post('/api/attendance/regularize', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { date, status, reason } = req.body;

      const record = await storage.createAttendanceRecord({
        userId,
        date,
        status,
        regularizationReason: reason,
        regularizedAt: new Date()
      });

      res.json(record);
    } catch (error) {
      console.error("Error regularizing attendance:", error);
      res.status(500).json({ message: "Failed to regularize attendance" });
    }
  });

  // Salary slip routes
  app.get('/api/salary-slips', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const slips = await storage.getSalarySlips(userId);
      res.json(slips);
    } catch (error) {
      console.error("Error fetching salary slips:", error);
      res.status(500).json({ message: "Failed to fetch salary slips" });
    }
  });

  app.get('/api/salary-slips/:month/:year', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { month, year } = req.params;
      const slip = await storage.getSalarySlip(userId, parseInt(month), parseInt(year));

      if (!slip) {
        return res.status(404).json({ message: "Salary slip not found" });
      }

      res.json(slip);
    } catch (error) {
      console.error("Error fetching salary slip:", error);
      res.status(500).json({ message: "Failed to fetch salary slip" });
    }
  });

  // HR Documents routes
  app.get('/api/hr-documents', isAuthenticated, async (req, res) => {
    try {
      const documents = await storage.getHrDocuments();
      res.json(documents);
    } catch (error) {
      console.error("Error fetching HR documents:", error);
      res.status(500).json({ message: "Failed to fetch HR documents" });
    }
  });

  app.post('/api/hr-documents/upload', isAuthenticated, upload.single('file'), async (req: any, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "No file uploaded" });
      }

      const userId = req.user.claims.sub;
      const { category } = req.body;

      // Read file content for processing
      const filePath = req.file.path;
      const fileContent = fs.readFileSync(filePath, 'utf8');

      // Process document for vectorization
      const chunks = await processDocumentForVectorization(fileContent, req.file.originalname);

      const document = await storage.createHrDocument({
        name: req.file.originalname,
        category: category || 'general',
        filePath: filePath,
        fileSize: req.file.size,
        mimeType: req.file.mimetype,
        uploadedBy: userId,
        vectorCount: chunks.length,
        processedAt: new Date()
      });

      res.json(document);
    } catch (error) {
      console.error("Error uploading HR document:", error);
      res.status(500).json({ message: "Failed to upload document" });
    }
  });

  app.delete('/api/hr-documents/:id', isAuthenticated, async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteHrDocument(id);
      res.json({ message: "Document deleted successfully" });
    } catch (error) {
      console.error("Error deleting HR document:", error);
      res.status(500).json({ message: "Failed to delete document" });
    }
  });

  // AI Assistant routes
  app.post('/api/ai/ask', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { question } = req.body;

      if (!question?.trim()) {
        return res.status(400).json({ message: "Question is required" });
      }

      // Get HR documents for context
      const documents = await storage.getHrDocuments();

      // Simulate document content (in real implementation, you'd extract text from files)
      const documentContext = documents.map(doc => ({
        name: doc.name,
        content: `HR Policy document: ${doc.name}. Category: ${doc.category}. This document contains company policies and procedures.`,
        category: doc.category
      }));

      const { answer, documentsUsed } = await askHRAssistant(question, documentContext);

      // Save conversation
      await storage.createAiConversation({
        userId,
        question,
        answer,
        documentsUsed
      });

      res.json({ answer, documentsUsed });
    } catch (error) {
      console.error("Error processing AI question:", error);
      const errorMessage = error instanceof Error ? error.message : "Failed to process your question. Please try again.";
      res.status(500).json({ message: errorMessage });
    }
  });

  app.get('/api/ai/conversations', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const conversations = await storage.getUserConversations(userId);
      res.json(conversations);
    } catch (error) {
      console.error("Error fetching conversations:", error);
      res.status(500).json({ message: "Failed to fetch conversations" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  leaves,
  leaveTypes,
  leaveBalances,
  attendanceRecords,
  salarySlips,
  hrDocuments,
  aiConversations,
  type User,
  type UpsertUser,
  type Leave,
  type InsertLeave,
  type LeaveType,
  type LeaveBalance,
  type AttendanceRecord,
  type InsertAttendanceRecord,
  type SalarySlip,
  type HrDocument,
  type InsertHrDocument,
  type AiConversation,
  type InsertAiConversation,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, gte, lte, desc, asc } from "drizzle-orm";

export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Leave operations
  getLeaveTypes(): Promise<LeaveType[]>;
  getLeaveBalances(userId: string, year: number): Promise<LeaveBalance[]>;
  createLeave(leave: InsertLeave): Promise<Leave>;
  getUserLeaves(userId: string): Promise<Leave[]>;
  updateLeave(id: string, updates: Partial<Leave>): Promise<Leave>;
  deleteLeave(id: string): Promise<void>;

  // Attendance operations
  getAttendanceRecords(userId: string, month: number, year: number): Promise<AttendanceRecord[]>;
  createAttendanceRecord(record: InsertAttendanceRecord): Promise<AttendanceRecord>;
  updateAttendanceRecord(id: string, updates: Partial<AttendanceRecord>): Promise<AttendanceRecord>;
  getAbsentDates(userId: string, days: number): Promise<AttendanceRecord[]>;

  // Salary operations
  getSalarySlips(userId: string): Promise<SalarySlip[]>;
  getSalarySlip(userId: string, month: number, year: number): Promise<SalarySlip | undefined>;

  // HR Document operations
  createHrDocument(document: InsertHrDocument): Promise<HrDocument>;
  getHrDocuments(): Promise<HrDocument[]>;
  updateHrDocument(id: string, updates: Partial<HrDocument>): Promise<HrDocument>;
  deleteHrDocument(id: string): Promise<void>;

  // AI Conversation operations
  createAiConversation(conversation: InsertAiConversation): Promise<AiConversation>;
  getUserConversations(userId: string): Promise<AiConversation[]>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async getLeaveTypes(): Promise<LeaveType[]> {
    return await db.select().from(leaveTypes);
  }

  async getLeaveBalances(userId: string, year: number): Promise<LeaveBalance[]> {
    return await db
      .select()
      .from(leaveBalances)
      .where(and(eq(leaveBalances.userId, userId), eq(leaveBalances.year, year)));
  }

  async createLeave(leave: InsertLeave): Promise<Leave> {
    const [newLeave] = await db
      .insert(leaves)
      .values(leave)
      .returning();
    return newLeave;
  }

  async getUserLeaves(userId: string): Promise<Leave[]> {
    return await db
      .select()
      .from(leaves)
      .where(eq(leaves.userId, userId))
      .orderBy(desc(leaves.appliedAt));
  }

  async updateLeave(id: string, updates: Partial<Leave>): Promise<Leave> {
    const [updatedLeave] = await db
      .update(leaves)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(leaves.id, id))
      .returning();
    return updatedLeave;
  }

  async deleteLeave(id: string): Promise<void> {
    await db.delete(leaves).where(eq(leaves.id, id));
  }

  async getAttendanceRecords(userId: string, month: number, year: number): Promise<AttendanceRecord[]> {
    const startDate = new Date(year, month - 1, 1);
    const endDate = new Date(year, month, 0);

    return await db
      .select()
      .from(attendanceRecords)
      .where(
        and(
          eq(attendanceRecords.userId, userId),
          gte(attendanceRecords.date, startDate.toISOString().split('T')[0]),
          lte(attendanceRecords.date, endDate.toISOString().split('T')[0])
        )
      )
      .orderBy(asc(attendanceRecords.date));
  }

  async createAttendanceRecord(record: InsertAttendanceRecord): Promise<AttendanceRecord> {
    const [newRecord] = await db
      .insert(attendanceRecords)
      .values(record)
      .returning();
    return newRecord;
  }

  async updateAttendanceRecord(id: string, updates: Partial<AttendanceRecord>): Promise<AttendanceRecord> {
    const [updatedRecord] = await db
      .update(attendanceRecords)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(attendanceRecords.id, id))
      .returning();
    return updatedRecord;
  }

  async getAbsentDates(userId: string, days: number): Promise<AttendanceRecord[]> {
    const pastDate = new Date();
    pastDate.setDate(pastDate.getDate() - days);

    return await db
      .select()
      .from(attendanceRecords)
      .where(
        and(
          eq(attendanceRecords.userId, userId),
          eq(attendanceRecords.status, 'absent'),
          gte(attendanceRecords.date, pastDate.toISOString().split('T')[0])
        )
      )
      .orderBy(desc(attendanceRecords.date));
  }

  async getSalarySlips(userId: string): Promise<SalarySlip[]> {
    return await db
      .select()
      .from(salarySlips)
      .where(eq(salarySlips.userId, userId))
      .orderBy(desc(salarySlips.year), desc(salarySlips.month));
  }

  async getSalarySlip(userId: string, month: number, year: number): Promise<SalarySlip | undefined> {
    const [slip] = await db
      .select()
      .from(salarySlips)
      .where(
        and(
          eq(salarySlips.userId, userId),
          eq(salarySlips.month, month),
          eq(salarySlips.year, year)
        )
      );
    return slip;
  }

  async createHrDocument(document: InsertHrDocument): Promise<HrDocument> {
    const [newDocument] = await db
      .insert(hrDocuments)
      .values(document)
      .returning();
    return newDocument;
  }

  async getHrDocuments(): Promise<HrDocument[]> {
    return await db
      .select()
      .from(hrDocuments)
      .where(eq(hrDocuments.isActive, true))
      .orderBy(desc(hrDocuments.createdAt));
  }

  async updateHrDocument(id: string, updates: Partial<HrDocument>): Promise<HrDocument> {
    const [updatedDocument] = await db
      .update(hrDocuments)
      .set(updates)
      .where(eq(hrDocuments.id, id))
      .returning();
    return updatedDocument;
  }

  async deleteHrDocument(id: string): Promise<void> {
    await db.update(hrDocuments)
      .set({ isActive: false })
      .where(eq(hrDocuments.id, id));
  }

  async createAiConversation(conversation: InsertAiConversation): Promise<AiConversation> {
    const [newConversation] = await db
      .insert(aiConversations)
      .values(conversation)
      .returning();
    return newConversation;
  }

  async getUserConversations(userId: string): Promise<AiConversation[]> {
    return await db
      .select()
      .from(aiConversations)
      .where(eq(aiConversations.userId, userId))
      .orderBy(desc(aiConversations.createdAt))
      .limit(50);
  }
}

export const storage = new DatabaseStorage();

