/* Custom component styles matching the design reference */
.stat-card {
  transition: all 0.3s ease;
}

.stat-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 12px 24px -10px rgba(0, 0, 0, 0.15);
}

.table-row:hover {
  background-color: hsl(var(--muted));
}

.form-input:focus {
  border-color: hsl(var(--ring));
  box-shadow: 0 0 0 3px hsl(var(--ring) / 0.1);
}

.btn-primary {
  background-color: hsl(var(--primary));
  color: hsl(var(--primary-foreground));
  transition: all 0.2s ease;
}

.btn-primary:hover {
  background-color: hsl(217 91% 50%);
  box-shadow: 0 4px 12px -2px rgba(59, 130, 246, 0.5);
}

.btn-secondary {
  background-color: hsl(var(--secondary));
  color: hsl(var(--secondary-foreground));
  transition: all 0.2s ease;
}

.btn-secondary:hover {
  background-color: hsl(214 32% 85%);
}

.file-upload-area {
  transition: all 0.3s ease;
}

.file-upload-area:hover {
  border-color: hsl(var(--primary));
  background-color: hsl(var(--primary) / 0.05);
}

.chat-message {
  animation: slideIn 0.3s ease-out;
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Uppy dashboard styles override */
.uppy-Dashboard-inner {
  border-radius: var(--radius) !important;
}

.uppy-Dashboard-AddFiles {
  border-color: hsl(var(--border)) !important;
}

.uppy-Dashboard-AddFiles:hover {
  border-color: hsl(var(--primary)) !important;
}

.uppy-c-btn--primary {
  background-color: hsl(var(--primary)) !important;
  color: hsl(var(--primary-foreground)) !important;
}

.uppy-c-btn--primary:hover {
  background-color: hsl(217 91% 50%) !important;
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

::-webkit-scrollbar-track {
  background: hsl(var(--muted));
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--muted-foreground) / 0.5);
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--muted-foreground) / 0.7);
}

