client/src/pages/LeaveCorrection.tsx.content:
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Edit, Trash2, Eye, UserCheck, Ban, Calendar } from "lucide-react";
import { format } from "date-fns";

export default function LeaveCorrection() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: leaves, isLoading } = useQuery({
    queryKey: ['/api/leaves'],
  });

  const cancelLeaveMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest('PUT', `/api/leaves/${id}`, { status: 'cancelled' });
    },
    onSuccess: () => {
      toast({
        title: "Leave Cancelled",
        description: "Your leave request has been cancelled successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/leaves'] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'approved':
        return 'bg-accent/10 text-accent';
      case 'pending':
        return 'bg-amber-500/10 text-amber-500';
      case 'rejected':
        return 'bg-destructive/10 text-destructive';
      case 'cancelled':
        return 'bg-muted text-muted-foreground';
      default:
        return 'bg-muted text-muted-foreground';
    }
  };

  const getLeaveTypeName = (typeId: string) => {
    switch (typeId) {
      case 'casual':
        return 'Casual Leave';
      case 'sick':
        return 'Sick Leave';
      case 'earned':
        return 'Earned Leave';
      default:
        return 'Leave';
    }
  };

  const canEdit = (status: string) => {
    return status === 'pending';
  };

  const canCancel = (status: string) => {
    return status === 'pending' || status === 'approved';
  };

  const filterLeavesByStatus = (status?: string) => {
    if (!leaves) return [];
    if (!status) return leaves;
    return leaves.filter((leave: any) => leave.status === status);
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <Card className="animate-pulse">
          <CardContent className="p-6">
            <div className="h-32 bg-muted rounded"></div>
          </CardContent>
        </Card>
      </div>
    );
  }

  const allLeaves = filterLeavesByStatus();
  const pendingLeaves = filterLeavesByStatus('pending');
  const approvedLeaves = filterLeavesByStatus('approved');
  const cancelledLeaves = filterLeavesByStatus('cancelled');

  const LeaveCard = ({ leave }: { leave: any }) => (
    <Card className="hover:shadow-lg transition-shadow">
      <CardContent className="p-6">
        <div className="flex items-start justify-between mb-4">
          <div className="flex-1">
            <div className="flex items-center gap-3 mb-2">
              <h4 className="text-lg font-semibold text-foreground">
                {getLeaveTypeName(leave.leaveTypeId)}
              </h4>
              <Badge className={getStatusColor(leave.status)}>
                {leave.status === 'pending' && 'Pending Approval'}
                {leave.status === 'approved' && 'Approved'}
                {leave.status === 'rejected' && 'Rejected'}
                {leave.status === 'cancelled' && 'Cancelled'}
              </Badge>
            </div>
            <p className="text-sm text-muted-foreground mb-3">
              {format(new Date(leave.fromDate), "MMM dd, yyyy")} - {format(new Date(leave.toDate), "MMM dd, yyyy")} 
              ({leave.days} {leave.days === "1" ? "day" : "days"}) â€¢ 
              Applied on {format(new Date(leave.appliedAt), "MMM dd, yyyy")}
            </p>
            <div className="bg-muted rounded-lg p-3 mb-4">
              <p className="text-sm text-foreground">
                <strong>Reason:</strong> {leave.reason}
              </p>
            </div>
            {leave.reviewedBy && (
              <div className="flex items-center gap-2 text-sm text-muted-foreground mb-4">
                <UserCheck className="w-4 h-4" />
                <span>
                  {leave.status === 'approved' ? 'Approved' : 'Reviewed'} by Manager on {' '}
                  {format(new Date(leave.reviewedAt), "MMM dd, yyyy")}
                </span>
              </div>
            )}
            {leave.status === 'cancelled' && (
              <div className="flex items-center gap-2 text-sm text-muted-foreground mb-4">
                <Ban className="w-4 h-4" />
                <span>Cancelled on {format(new Date(leave.updatedAt), "MMM dd, yyyy")}</span>
              </div>
            )}
          </div>
          <div className="flex items-center gap-2 ml-4">
            <Button variant="ghost" size="sm">
              <Eye className="w-4 h-4" />
            </Button>
            {canEdit(leave.status) && (
              <Button variant="ghost" size="sm">
                <Edit className="w-4 h-4 text-primary" />
              </Button>
            )}
            {canCancel(leave.status) && (
              <Button 
                variant="ghost" 
                size="sm"
                onClick={() => cancelLeaveMutation.mutate(leave.id)}
                disabled={cancelLeaveMutation.isPending}
              >
                <Trash2 className="w-4 h-4 text-destructive" />
              </Button>
            )}
          </div>
        </div>

        {canEdit(leave.status) && (
          <div className="flex items-center gap-4">
            <Button className="px-4">
              <Edit className="w-4 h-4 mr-2" />
              Edit Request
            </Button>
            <Button 
              variant="outline" 
              className="px-4 border-destructive text-destructive hover:bg-destructive hover:text-destructive-foreground"
              onClick={() => cancelLeaveMutation.mutate(leave.id)}
              disabled={cancelLeaveMutation.isPending}
            >
              <Trash2 className="w-4 h-4 mr-2" />
              Cancel Request
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">Manage Leave Requests</CardTitle>
          <p className="text-sm text-muted-foreground">
            Edit or cancel your pending and approved leave requests
          </p>
        </CardHeader>
      </Card>

      <Tabs defaultValue="all" className="w-full">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="all">All Requests ({allLeaves.length})</TabsTrigger>
          <TabsTrigger value="pending">Pending ({pendingLeaves.length})</TabsTrigger>
          <TabsTrigger value="approved">Approved ({approvedLeaves.length})</TabsTrigger>
          <TabsTrigger value="cancelled">Cancelled ({cancelledLeaves.length})</TabsTrigger>
        </TabsList>

        <TabsContent value="all" className="space-y-4">
          {allLeaves.length > 0 ? (
            allLeaves.map((leave: any) => (
              <LeaveCard key={leave.id} leave={leave} />
            ))
          ) : (
            <Card>
              <CardContent className="p-8 text-center">
                <Calendar className="w-12 h-12 text-muted-foreground mx-auto mb-3" />
                <p className="text-muted-foreground">No leave requests found</p>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="pending" className="space-y-4">
          {pendingLeaves.length > 0 ? (
            pendingLeaves.map((leave: any) => (
              <LeaveCard key={leave.id} leave={leave} />
            ))
          ) : (
            <Card>
              <CardContent className="p-8 text-center">
                <Calendar className="w-12 h-12 text-muted-foreground mx-auto mb-3" />
                <p className="text-muted-foreground">No pending leave requests</p>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="approved" className="space-y-4">
          {approvedLeaves.length > 0 ? (
            approvedLeaves.map((leave: any) => (
              <LeaveCard key={leave.id} leave={leave} />
            ))
          ) : (
            <Card>
              <CardContent className="p-8 text-center">
                <Calendar className="w-12 h-12 text-muted-foreground mx-auto mb-3" />
                <p className="text-muted-foreground">No approved leave requests</p>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="cancelled" className="space-y-4">
          {cancelledLeaves.length > 0 ? (
            cancelledLeaves.map((leave: any) => (
              <LeaveCard key={leave.id} leave={leave} />
            ))
          ) : (
            <Card>
              <CardContent className="p-8 text-center">
                <Calendar className="w-12 h-12 text-muted-foreground mx-auto mb-3" />
                <p className="text-muted-foreground">No cancelled leave requests</p>
              </CardContent>
            </Card>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/LeaveSummary.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from "@/components/ui/table";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Umbrella, Thermometer, Trophy, Calendar, Eye } from "lucide-react";
import { format } from "date-fns";

export default function LeaveSummary() {
  const { data: leaves, isLoading: leavesLoading } = useQuery({
    queryKey: ['/api/leaves'],
  });

  const { data: leaveBalances, isLoading: balancesLoading } = useQuery({
    queryKey: ['/api/leave-balances'],
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'approved':
        return 'bg-accent/10 text-accent';
      case 'pending':
        return 'bg-amber-500/10 text-amber-500';
      case 'rejected':
        return 'bg-destructive/10 text-destructive';
      case 'cancelled':
        return 'bg-muted text-muted-foreground';
      default:
        return 'bg-muted text-muted-foreground';
    }
  };

  const getLeaveTypeColor = (type: string) => {
    switch (type) {
      case 'casual':
        return 'bg-primary';
      case 'sick':
        return 'bg-accent';
      case 'earned':
        return 'bg-blue-500';
      default:
        return 'bg-muted';
    }
  };

  if (leavesLoading || balancesLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {[1, 2, 3].map((i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-6">
                <div className="h-32 bg-muted rounded"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Leave Balance Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {leaveBalances?.map((balance: any) => {
          const used = balance.usedDays || 0;
          const total = balance.totalDays || 0;
          const remaining = total - used;
          const percentage = total > 0 ? (used / total) * 100 : 0;

          const getLeaveTypeDetails = (leaveTypeId: string) => {
            switch (leaveTypeId) {
              case 'casual':
                return {
                  name: 'Casual Leave',
                  icon: Umbrella,
                  color: 'text-primary',
                  bgColor: 'bg-primary/10'
                };
              case 'sick':
                return {
                  name: 'Sick Leave',
                  icon: Thermometer,
                  color: 'text-accent',
                  bgColor: 'bg-accent/10'
                };
              case 'earned':
                return {
                  name: 'Earned Leave',
                  icon: Trophy,
                  color: 'text-blue-500',
                  bgColor: 'bg-blue-500/10'
                };
              default:
                return {
                  name: 'Leave',
                  icon: Calendar,
                  color: 'text-muted-foreground',
                  bgColor: 'bg-muted/10'
                };
            }
          };

          const leaveType = getLeaveTypeDetails(balance.leaveTypeId);
          const Icon = leaveType.icon;

          return (
            <Card key={balance.id} className="hover:shadow-lg transition-shadow">
              <CardContent className="p-6">
                <div className="flex items-center justify-between mb-4">
                  <div className={`p-3 rounded-lg ${leaveType.bgColor}`}>
                    <Icon className={`w-6 h-6 ${leaveType.color}`} />
                  </div>
                  <span className="text-2xl font-bold text-foreground">{remaining}</span>
                </div>
                <h4 className="font-semibold text-foreground mb-1">{leaveType.name}</h4>
                <p className="text-sm text-muted-foreground mb-3">
                  {used} used of {total} allocated
                </p>
                <Progress value={percentage} className="h-2" />
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Leave History */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg">Leave History</CardTitle>
            <div className="flex items-center gap-3">
              <Select defaultValue="all">
                <SelectTrigger className="w-32">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="casual">Casual Leave</SelectItem>
                  <SelectItem value="sick">Sick Leave</SelectItem>
                  <SelectItem value="earned">Earned Leave</SelectItem>
                </SelectContent>
              </Select>
              <Select defaultValue="2024">
                <SelectTrigger className="w-20">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="2024">2024</SelectItem>
                  <SelectItem value="2023">2023</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {leaves && leaves.length > 0 ? (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Leave Type</TableHead>
                    <TableHead>From Date</TableHead>
                    <TableHead>To Date</TableHead>
                    <TableHead>Days</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {leaves.map((leave: any) => (
                    <TableRow key={leave.id} className="hover:bg-muted/50">
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <div 
                            className={`w-2 h-2 rounded-full ${getLeaveTypeColor(leave.leaveTypeId)}`}
                          />
                          <span className="text-sm font-medium">
                            {leave.leaveTypeId === 'casual' ? 'Casual Leave' :
                             leave.leaveTypeId === 'sick' ? 'Sick Leave' :
                             leave.leaveTypeId === 'earned' ? 'Earned Leave' : 'Leave'}
                          </span>
                        </div>
                      </TableCell>
                      <TableCell className="text-sm text-muted-foreground">
                        {format(new Date(leave.fromDate), "MMM dd, yyyy")}
                      </TableCell>
                      <TableCell className="text-sm text-muted-foreground">
                        {format(new Date(leave.toDate), "MMM dd, yyyy")}
                      </TableCell>
                      <TableCell className="text-sm font-medium">
                        {leave.days}
                      </TableCell>
                      <TableCell>
                        <Badge 
                          className={`${getStatusColor(leave.status)} capitalize`}
                        >
                          {leave.status}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        <Button 
                          variant="ghost" 
                          size="sm"
                          className="text-primary hover:text-primary/80"
                        >
                          <Eye className="w-4 h-4 mr-1" />
                          View
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          ) : (
            <div className="text-center py-8">
              <Calendar className="w-12 h-12 text-muted-foreground mx-auto mb-3" />
              <p className="text-muted-foreground">No leave history available</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/RegularizeAttendance.ts.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Checkbox } from "@/components/ui/checkbox";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { z } from "zod";
import { Check, Home, MapPin, Calendar } from "lucide-react";
import { format } from "date-fns";

const regularizeSchema = z.object({
  date: z.string().min(1, "Date is required"),
  status: z.enum(["wfh", "leave", "outdoor"], {
    required_error: "Please select a regularization type",
  }),
  reason: z.string().min(10, "Reason must be at least 10 characters"),
});

type RegularizeForm = z.infer<typeof regularizeSchema>;

export default function RegularizeAttendance() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedAbsentDates, setSelectedAbsentDates] = useState<string[]>([]);

  const form = useForm<RegularizeForm>({
    resolver: zodResolver(regularizeSchema),
    defaultValues: {
      date: "",
      status: undefined,
      reason: "",
    },
  });

  const { data: absentDates, isLoading } = useQuery({
    queryKey: ['/api/attendance/absent-dates', { days: 7 }],
  });

  const regularizeMutation = useMutation({
    mutationFn: async (data: RegularizeForm) => {
      return await apiRequest('POST', '/api/attendance/regularize', data);
    },
    onSuccess: () => {
      toast({
        title: "Attendance Regularized",
        description: "Your attendance has been regularized successfully.",
      });
      form.reset();
      queryClient.invalidateQueries({ queryKey: ['/api/attendance'] });
      queryClient.invalidateQueries({ queryKey: ['/api/attendance/absent-dates'] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: RegularizeForm) => {
    regularizeMutation.mutate(data);
  };

  const toggleAbsentDate = (date: string) => {
    setSelectedAbsentDates(prev => 
      prev.includes(date) 
        ? prev.filter(d => d !== date)
        : [...prev, date]
    );
  };

  const regularizationTypes = [
    {
      value: "wfh",
      title: "Work from Home",
      description: "Mark as WFH",
      icon: Home,
      color: "text-blue-500"
    },
    {
      value: "leave",
      title: "Apply Leave",
      description: "Convert to leave",
      icon: Calendar,
      color: "text-primary"
    },
    {
      value: "outdoor",
      title: "Outdoor Duty",
      description: "Client/field visit",
      icon: MapPin,
      color: "text-accent"
    }
  ];

  if (isLoading) {
    return (
      <div className="max-w-4xl">
        <Card>
          <CardContent className="p-6">
            <div className="animate-pulse space-y-4">
              <div className="h-8 bg-muted rounded w-1/3"></div>
              <div className="h-32 bg-muted rounded"></div>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="max-w-4xl space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="text-xl">Regularize Attendance</CardTitle>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              {/* Date Selection */}
              <FormField
                control={form.control}
                name="date"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Select Date(s) <span className="text-destructive">*</span></FormLabel>
                    <FormControl>
                      <Input type="date" {...field} />
                    </FormControl>
                    <p className="text-xs text-muted-foreground">
                      You can only regularize dates from the last 7 days
                    </p>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Recent Absent Dates */}
              {absentDates && absentDates.length > 0 && (
                <Card className="bg-muted/50">
                  <CardContent className="pt-4">
                    <h4 className="font-semibold text-foreground mb-3">Recent Absent Dates</h4>
                    <div className="space-y-2">
                      {absentDates.map((record: any) => (
                        <div 
                          key={record.id} 
                          className="flex items-center justify-between p-3 bg-card rounded-lg"
                        >
                          <div className="flex items-center gap-3">
                            <Checkbox 
                              checked={selectedAbsentDates.includes(record.date)}
                              onCheckedChange={() => toggleAbsentDate(record.date)}
                            />
                            <div>
                              <p className="text-sm font-medium text-foreground">
                                {format(new Date(record.date), "MMMM dd, yyyy")}
                              </p>
                              <p className="text-xs text-destructive">Absent - Not marked</p>
                            </div>
                          </div>
                          <span className="text-xs text-muted-foreground">
                            {format(new Date(record.date), "EEEE")}
                          </span>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* Regularization Type */}
              <FormField
                control={form.control}
                name="status"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Regularization Type <span className="text-destructive">*</span></FormLabel>
                    <FormControl>
                      <RadioGroup 
                        onValueChange={field.onChange} 
                        defaultValue={field.value}
                        className="grid grid-cols-1 md:grid-cols-3 gap-3"
                      >
                        {regularizationTypes.map((type) => {
                          const Icon = type.icon;
                          return (
                            <div key={type.value} className="flex items-center space-x-2">
                              <RadioGroupItem value={type.value} id={type.value} />
                              <Label 
                                htmlFor={type.value}
                                className="flex items-center gap-3 p-4 border border-input rounded-lg cursor-pointer hover:border-primary transition-colors flex-1"
                              >
                                <Icon className={`w-5 h-5 ${type.color}`} />
                                <div>
                                  <p className="font-medium text-sm">{type.title}</p>
                                  <p className="text-xs text-muted-foreground">{type.description}</p>
                                </div>
                              </Label>
                            </div>
                          );
                        })}
                      </RadioGroup>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Reason */}
              <FormField
                control={form.control}
                name="reason"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Reason <span className="text-destructive">*</span></FormLabel>
                    <FormControl>
                      <Textarea 
                        {...field} 
                        rows={4} 
                        placeholder="Explain why you need to regularize attendance..."
                        className="resize-none"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Actions */}
              <div className="flex items-center gap-4">
                <Button 
                  type="submit" 
                  disabled={regularizeMutation.isPending}
                  className="px-6"
                >
                  <Check className="w-4 h-4 mr-2" />
                  {regularizeMutation.isPending ? "Submitting..." : "Submit Regularization"}
                </Button>
                <Button 
                  type="button" 
                  variant="outline"
                  onClick={() => form.reset()}
                >
                  Cancel
                </Button>
              </div>
            </form>
          </Form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/SalarySlips.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Download, Eye, FileText, Calendar } from "lucide-react";
import { format } from "date-fns";

export default function SalarySlips() {
  const [selectedYear, setSelectedYear] = useState("2024");

  const { data: salarySlips, isLoading } = useQuery({
    queryKey: ['/api/salary-slips'],
  });

  const getMonthName = (month: number) => {
    const months = [
      "January", "February", "March", "April", "May", "June",
      "July", "August", "September", "October", "November", "December"
    ];
    return months[month - 1];
  };

  const getGradientColor = (index: number) => {
    const gradients = [
      "from-primary to-blue-600",
      "from-accent to-green-600", 
      "from-purple-500 to-purple-700",
      "from-blue-500 to-blue-700",
      "from-amber-500 to-orange-600",
      "from-pink-500 to-rose-600"
    ];
    return gradients[index % gradients.length];
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[1, 2, 3].map((i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-6">
                <div className="h-48 bg-muted rounded"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  const filteredSlips = salarySlips?.filter((slip: any) => 
    slip.year.toString() === selectedYear
  ) || [];

  // Calculate YTD summary
  const ytdSummary = filteredSlips.reduce((acc: any, slip: any) => ({
    gross: acc.gross + parseFloat(slip.grossSalary || 0),
    net: acc.net + parseFloat(slip.netSalary || 0),
    deductions: acc.deductions + (parseFloat(slip.grossSalary || 0) - parseFloat(slip.netSalary || 0))
  }), { gross: 0, net: 0, deductions: 0 });

  const averageMonthly = filteredSlips.length > 0 ? ytdSummary.net / filteredSlips.length : 0;

  return (
    <div className="space-y-6">
      {/* Search and Filter */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row items-start md:items-center justify-between gap-4">
            <div>
              <h3 className="text-lg font-semibold text-foreground mb-1">Salary Slips</h3>
              <p className="text-sm text-muted-foreground">View and download your monthly salary slips</p>
            </div>
            <div className="flex items-center gap-3">
              <Select defaultValue="all">
                <SelectTrigger className="w-32">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Months</SelectItem>
                  {Array.from({ length: 12 }, (_, i) => (
                    <SelectItem key={i} value={(i + 1).toString()}>
                      {getMonthName(i + 1)} {selectedYear}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Select value={selectedYear} onValueChange={setSelectedYear}>
                <SelectTrigger className="w-20">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="2024">2024</SelectItem>
                  <SelectItem value="2023">2023</SelectItem>
                  <SelectItem value="2022">2022</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Salary Slips Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredSlips.length > 0 ? (
          filteredSlips.map((slip: any, index: number) => (
            <Card key={slip.id} className="hover:shadow-lg transition-shadow overflow-hidden">
              <div className={`bg-gradient-to-br ${getGradientColor(index)} p-6 text-primary-foreground`}>
                <div className="flex items-start justify-between mb-4">
                  <div>
                    <p className="text-sm opacity-90 mb-1">Salary Slip</p>
                    <h4 className="text-2xl font-bold">
                      {getMonthName(slip.month)} {slip.year}
                    </h4>
                  </div>
                  <div className="w-12 h-12 bg-white/20 rounded-lg flex items-center justify-center">
                    <FileText className="w-6 h-6" />
                  </div>
                </div>
                <div className="space-y-1">
                  <p className="text-sm opacity-90">Net Salary</p>
                  <p className="text-3xl font-bold">
                    {formatCurrency(parseFloat(slip.netSalary || 0))}
                  </p>
                </div>
              </div>
              <CardContent className="p-6">
                <div className="space-y-3 mb-4">
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Gross Salary:</span>
                    <span className="font-semibold text-foreground">
                      {formatCurrency(parseFloat(slip.grossSalary || 0))}
                    </span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span className="text-muted-foreground">Deductions:</span>
                    <span className="font-semibold text-destructive">
                      -{formatCurrency(parseFloat(slip.grossSalary || 0) - parseFloat(slip.netSalary || 0))}
                    </span>
                  </div>
                  <div className="border-t border-border pt-3">
                    <div className="flex justify-between text-sm">
                      <span className="text-muted-foreground">Payment Date:</span>
                      <span className="font-medium text-foreground">
                        {slip.paymentDate ? format(new Date(slip.paymentDate), "MMM dd, yyyy") : "N/A"}
                      </span>
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-3">
                  <Button className="flex-1 px-4" size="sm">
                    <Download className="w-4 h-4 mr-2" />
                    Download PDF
                  </Button>
                  <Button variant="outline" size="sm" className="p-2">
                    <Eye className="w-4 h-4" />
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))
        ) : (
          <div className="col-span-full">
            <Card>
              <CardContent className="p-8 text-center">
                <FileText className="w-12 h-12 text-muted-foreground mx-auto mb-3" />
                <p className="text-muted-foreground">No salary slips found for {selectedYear}</p>
              </CardContent>
            </Card>
          </div>
        )}
      </div>

      {/* Year-to-Date Summary */}
      {filteredSlips.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Year-to-Date Summary ({selectedYear})</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              <div>
                <p className="text-sm text-muted-foreground mb-1">Total Gross Pay</p>
                <p className="text-2xl font-bold text-foreground">
                  {formatCurrency(ytdSummary.gross)}
                </p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground mb-1">Total Deductions</p>
                <p className="text-2xl font-bold text-destructive">
                  {formatCurrency(ytdSummary.deductions)}
                </p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground mb-1">Total Net Pay</p>
                <p className="text-2xl font-bold text-accent">
                  {formatCurrency(ytdSummary.net)}
                </p>
              </div>
              <div>
                <p className="text-sm text-muted-foreground mb-1">Average Monthly</p>
                <p className="text-2xl font-bold text-primary">
                  {formatCurrency(averageMonthly)}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

